import { useNavigate } from "react-router-dom";
import { Backbtn, GreyBanana, GreyScore, Yesbtn } from "../assets/svgs/svgs";
import { useDispatch, useSelector } from "react-redux";
import { changeRotate, matching, notmatching } from "../redux-tool/Slice";
import PlayCard from "../common/PlayCard";
import { useEffect, useState } from "react";
function GamePage() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const allcards = useSelector((state: any) => state.memorygame.game);
  const [clicking, setClicking] = useState(0);
  const [win, setWin] = useState("Loose");
  const [show, setShow] = useState("");
  const flipfunc = (e: any) => {
    setShow(e);
    if (show.match === e.match) {
      let data = { s1: show, s2: e };
      setTimeout(() => {
        dispatch(matching(data));
      }, 1000);
      setShow("");
    }
    dispatch(changeRotate(e));
  };
  const flipfuncback = (e: any) => {
    setClicking(clicking + 1);
    let data = { s1: show, s2: e };
    if (show.match === e.match) {
      dispatch(changeRotate(e));
      setTimeout(() => {
        dispatch(matching(data));
      }, 1000);
      setShow("");
    } else {
      dispatch(changeRotate(e));
      setTimeout(() => {
        dispatch(notmatching(data));
      }, 1000);
      setShow("");
    }
  };

  useEffect(() => {
    const func = () => {
      let counts = 2;
      allcards.map((i: any) => {
        if (i.match === "match") {
          counts = counts + 1;
        }
      });
      if (counts === allcards.length) {
        setWin("Win");
      }
    };
    func();
  }, [clicking]);
  const handleClick = (cardValue: any) => {
    if (show === "") {
      flipfunc(cardValue);
    } else {
      flipfuncback(cardValue);
    }
  };
  return (
    <div className="container d-flex-center h-100p">
      <div className="button back" onClick={() => navigate("/next")}>
        {<Backbtn height="146" width="146" />}
      </div>
      <div className="score-level">
        <GreyScore height="37" width="755" />
        <div className="score-level-abs">
          <GreyBanana height="139" width="134" />
        </div>
      </div>

      <div className="boxsdiv">
        {allcards.map((value: any, index: number) => {
          if (index <= 7)
            return <PlayCard key={value.id} value={value} handleClick={handleClick} />;
        })}
      </div>

      <div className="boxsdiv">
        {allcards.map((value: any, index: number) => {
          if (index > 7)
            return <PlayCard key={value.id} value={value} handleClick={handleClick} />;
        })}
      </div>
      <div className="button down">{<Yesbtn height="90" width="339" />}</div>
    </div>
  );
}

export default GamePage;
